import "./App.css";
import { useState } from "react";
import Axios from "axios";

function App() {
  const [name, setName] = useState("");
  const [plz, setPLZ] = useState(0);
  const [ort, setOrt] = useState("");
  const [strasse, setStrasse] = useState("");
  const [telefon, setTelefon] = useState(0);

  // Sendet die Daten aus dem FrontEnd zum BackEnd
  // Datei:     tierheim/server/index.js
  // Funktion:  app.post("/addTierheim", (req, res) => {...})
  const addTierheim = () => {
    Axios.post("http://localhost:3001/addTierheim", {
      name: name,
      plz: plz,
      ort: ort,
      strasse: strasse,
      telefon: telefon,
    });
  };

  /*
  const addEmployee = () => {
    Axios.post("http://localhost:3001/create", {
      name: name,
      age: age,
      country: country,
      position: position,
      wage: wage,
    }).then(() => {
      setEmployeeList([
        ...employeeList,
        {
          name: name,
          age: age,
          country: country,
          position: position,
          wage: wage,
        },
      ]);
    });
  };
  */

  return (
    <div className="App">
      <div className="information">
        <label>Tierheim:</label>
        <input
          type="text"
          onChange={(event) => {
            setName(event.target.value);
          }}
        />
        <label>PLZ:</label>
        <input
          type="number"
          onChange={(event) => {
            setPLZ(event.target.value);
          }}
        />
        <label>Ort:</label>
        <input
          type="text"
          onChange={(event) => {
            setOrt(event.target.value);
          }}
        />
        <label>Straße:</label>
        <input
          type="text"
          onChange={(event) => {
            setStrasse(event.target.value);
          }}
        />
        <label>Telefonnummer:</label>
        <input
          type="number"
          onChange={(event) => {
            setTelefon(event.target.value);
          }}
        />
        <button onClick={addTierheim}>Tierheim hinzufügen</button>
      </div>
    </div>
  );
}

export default App;
